import classes from './MealItem.module.css'
import MealItemForm from './MealItemForm';
import CartContext from '../../store/cart-context';
import {useContext} from 'react';

function MealItem(props) {
    const ctx = useContext(CartContext);
    const price = `$${props.price.toFixed(2)}`
    function onAmountHandler(amount){
        ctx.addItem(
            {name: props.name}
        )
        ctx.totalAmount= ctx.totalAmount+props.price 
    }

    function getItemNumber(itemNumber){
        if (ctx.items.includes(props.meal) === false){
          ctx.items.concat(props.meal)
        }
        ctx.totalAmount= ctx.totalAmount+ itemNumber*props.price
    }

    return (
        <li className={classes.meal}>
            <div>
                <h3>{props.name}</h3>
             <div className={classes.description}>{props.description}</div>
            <div className={classes.price}>{price}</div>
            </div>
            <MealItemForm props={getItemNumber} onAmount={onAmountHandler}/> 
        </li>
    )
}

export default MealItem;
