import React from 'react';

function CartProvider(props){
    const contextValue= {
        items:[],
        totalAmount:0,
        addItem: (item)=>{},
        removeItem: (id) => {}
    }
    const CartContext = React.createContext(contextValue)

    return <CartContext.Provider value={contextValue}>
        {props.children}
    </CartContext.Provider>
}

export default CartProvider;
