import {useState, useRef, Fragment} from 'react';
import classes from './MealItemForm.module.css'
import Input from '../Input'

function MealItemForm() {
    let itemCount;
    const inputRef = useRef(null);
    const [itemInput,setItemInput] = useState(0);
   function submitHandler(event) {
       event.preventDefault();
       console.log(inputRef.current.value); 
       setItemInput(inputRef.current.value)

   }
   return(
       <Fragment>
       <form className={classes.form}onSubmit={submitHandler}>
       <div className={classes.input}>
           <label>
           </label>
           <input className={classes.input} type='number' min='0' max='99' ref={inputRef}>{itemCount}</input>
       </div>
           <button type='submit'>+ Add</button>
       </form>
       <div className={classes.itemCount}>{`itemCount:${itemInput}`}</div>
       </Fragment>
   )
}

export default MealItemForm;
